// src/services/signalRConnection.js
import * as signalR from '@microsoft/signalr';

const connection = new signalR.HubConnectionBuilder()
  .withUrl("http://localhost:5090/chathub") // your backend hub URL
  .withAutomaticReconnect()
  .build();

async function startConnection() {
  if (connection.state === signalR.HubConnectionState.Disconnected) {
    try {
      await connection.start();
      console.log("SignalR Connected.");
    } catch (err) {
      console.error("Connection failed: ", err);
      setTimeout(startConnection, 2000); // retry after 2 seconds
    }
  } else {
    console.log("Connection already started or connecting.");
  }
}

export { connection, startConnection };
