import React, { useEffect, useState } from "react";
import axios from "axios";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = () => {
  const [user, setUser] = useState(null); // logged-in user
  const [users, setUsers] = useState([]); // all users
  const [selectedUser, setSelectedUser] = useState(null); // chat partner
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    startConnection();
    connection.on("ReceiveMessage", (senderId, messageText) => {
      setChat(prev => [...prev, { senderId, message: messageText }]);
    });

    return () => connection.off("ReceiveMessage");
  }, []);

  async function login(username, password) {
    try {
      const res = await axios.post("http://localhost:5090/api/users/login", { username, password });
      setUser(res.data);
      const usersRes = await axios.get("http://localhost:5090/api/users");
      setUsers(usersRes.data.filter(u => u.id !== res.data.id)); // remove self
      await connection.invoke("Login", res.data.id);
    } catch (err) {
      console.error(err);
      alert("Login failed");
    }
  }

  async function sendMessage() {
    if (!message.trim() || !selectedUser) return;
    if (connection.state === 1) { // Connected
      await connection.invoke("SendMessage", selectedUser.id, message);
      setMessage("");
    } else {
      console.warn("Connection not ready");
    }
  }

  if (!user) {
    return (
      <div>
        <input placeholder="Username" id="username" />
        <input placeholder="Password" id="password" type="password" />
        <button onClick={() => login(
          document.getElementById("username").value,
          document.getElementById("password").value
        )}>Login</button>
      </div>
    );
  }

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* User list */}
      <div style={{ width: "200px", borderRight: "1px solid #ccc" }}>
        {users.map(u => (
          <div key={u.id} style={{ padding: "8px", cursor: "pointer", backgroundColor: selectedUser?.id === u.id ? "#eee" : "" }}
               onClick={() => setSelectedUser(u)}>
            {u.username}
          </div>
        ))}
      </div>

      {/* Chat window */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div style={{ flex: 1, overflowY: "auto", padding: "8px" }}>
          {chat.filter(c => c.senderId === selectedUser?.id || c.senderId === user.id)
               .map((c, i) => (
            <div key={i} style={{ textAlign: c.senderId === user.id ? "right" : "left" }}>
              <strong>{c.senderId === user.id ? "You" : selectedUser.username}:</strong> {c.message}
            </div>
          ))}
        </div>
        <div style={{ display: "flex" }}>
          <input
            style={{ flex: 1 }}
            value={message}
            onChange={e => setMessage(e.target.value)}
            onKeyDown={e => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default ChatWindow;
