import React, { useEffect, useState, useRef } from "react";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = ({ currentUser, onLogout }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState("");
  const [allChats, setAllChats] = useState({});
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [allChats, selectedUser]);

  useEffect(() => {
    const init = async () => {
      try {
        if (connection.state !== "Connected") await startConnection();
        await connection.invoke("Login", currentUser.id);

        connection.off("ReceiveMessage");
        connection.on("ReceiveMessage", (senderId, receiverId, messageText, messageType, fileName) => {
          const otherId = senderId === currentUser.id ? receiverId : senderId;
          const msg = { senderId, message: messageText, type: messageType, fileName };
          setAllChats(prev => ({
            ...prev,
            [otherId]: [...(prev[otherId] || []), msg],
          }));
        });

        const res = await fetch("http://localhost:5090/api/users");
        const data = await res.json();
        setUsers(data.filter(u => u.id !== currentUser.id));
      } catch (err) {
        console.error("SignalR init failed:", err);
      }
    };

    init();
    return () => connection.off("ReceiveMessage");
  }, [currentUser]);

  const sendMessage = async (msg, type = "text", fileName = null) => {
    if ((!msg && type === "text") || !selectedUser) return;
    if (type === "text") setMessage("");
    await connection.invoke("SendMessage", selectedUser.id, msg, type, fileName);
  };

  const fetchMessages = async (user) => {
    const res = await fetch(`http://localhost:5090/api/users/messages/${currentUser.id}/${user.id}`);
    const msgs = await res.json();
    const formatted = msgs.map(m => ({
      senderId: m.senderId,
      message: m.type === "text" ? m.text : m.fileBase64,
      type: m.type,
      fileName: m.fileName
    }));
    setAllChats(prev => ({ ...prev, [user.id]: formatted }));
  };

  const handleUserClick = async (u) => {
    setSelectedUser(u);
    await fetchMessages(u);
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file || !selectedUser) return;

    const formData = new FormData();
    formData.append("file", file);
    formData.append("senderId", currentUser.id);
    formData.append("receiverId", selectedUser.id);

    const res = await fetch("http://localhost:5090/api/files/upload", { method: "POST", body: formData });
    const data = await res.json();

    await sendMessage(data.fileBase64, data.type, file.name);
    e.target.value = null;
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <div className="w-1/4 border-r p-4 bg-white shadow overflow-y-auto">
        <h2 className="font-bold mb-4">{currentUser.username}</h2>
        {users.map(u => (
          <div key={u.id} onClick={() => handleUserClick(u)}
               className={`p-2 cursor-pointer rounded ${selectedUser?.id === u.id ? "bg-blue-200" : "hover:bg-gray-200"}`}>
            {u.username}
          </div>
        ))}
      </div>

      <div className="flex-1 flex flex-col p-4">
        {selectedUser ? (
          <>
            <div className="flex-1 overflow-y-auto space-y-2">
              {(allChats[selectedUser.id] || []).map((c, i) => (
                <div key={i} className={`p-2 rounded-lg ${c.senderId === currentUser.id ? "self-end bg-blue-500 text-white" : "self-start bg-gray-200"}`}>
                  {c.type === "text" && <span>{c.message}</span>}
                  {c.type === "image" && (
                    <img src={c.message} alt="img" className="rounded-lg w-[250px] h-[200px] object-cover" />
                  )}
                  {c.type === "file" && (
                    <a href={c.message} download={c.fileName} className="underline text-blue-600">{c.fileName}</a>
                  )}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            <div className="flex items-center mt-2 space-x-2">
              <input
                className="flex-1 border rounded-full px-3 py-2"
                placeholder="Type message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage(message, "text")}
              />
              <input type="file" onChange={handleFileChange} accept="image/*" />
              <button onClick={() => sendMessage(message, "text")} className="bg-blue-500 text-white px-4 py-2 rounded-full">Send</button>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-500">Select a user</div>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
