import React, { useEffect, useState } from "react";
import { connection, startConnection } from "../services/signalRConnection";
import * as signalR from "@microsoft/signalr";

const ChatWindow = () => {
  const [userId, setUserId] = useState(0);
  const [users, setUsers] = useState([]); // all users
  const [receiverId, setReceiverId] = useState(0);
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const init = async () => {
      await startConnection();

      connection.on("ReceiveMessage", (senderId, message) => {
        setChat((prev) => [...prev, { senderId, message }]);
      });

      connection.on("UserLoggedIn", (userId) => {
        setUsers((prev) => {
          if (!prev.includes(userId)) return [...prev, userId];
          return prev;
        });
      });
    };
    init();
    return () => connection.off("ReceiveMessage");
  }, []);

  const login = async () => {
    if (userId <= 0) return alert("Enter valid user ID");
    try {
      await connection.invoke("Login", userId);
      setLoggedIn(true);
    } catch (err) {
      console.error(err);
    }
  };

  const sendMessage = async () => {
    if (!message.trim()) return;
    if (connection.state !== signalR.HubConnectionState.Connected) return;

    try {
      await connection.invoke("SendMessage", receiverId, message);
      setMessage("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="flex h-screen">
      {/* User list */}
      <div className="w-1/4 border-r p-2">
        <h3 className="font-bold">Users</h3>
        <ul>
          {users.map((u) => (
            <li
              key={u}
              className={`p-2 cursor-pointer ${
                u === receiverId ? "bg-gray-300" : ""
              }`}
              onClick={() => setReceiverId(u)}
            >
              User {u}
            </li>
          ))}
        </ul>
      </div>

      {/* Chat window */}
      <div className="flex-1 flex flex-col p-2">
        {!loggedIn ? (
          <div>
            <input
              type="number"
              placeholder="Your user ID"
              value={userId}
              onChange={(e) => setUserId(parseInt(e.target.value))}
              className="border p-2 mr-2"
            />
            <button className="bg-green-500 text-white p-2" onClick={login}>
              Login
            </button>
          </div>
        ) : (
          <>
            <div className="flex-1 overflow-y-auto border p-2">
              {chat.map((c, i) => (
                <div
                  key={i}
                  className={`p-1 ${
                    c.senderId === userId ? "text-right" : "text-left"
                  }`}
                >
                  <strong>{c.senderId}: </strong>
                  {c.message}
                </div>
              ))}
            </div>
            <div className="flex mt-2">
              <input
                type="text"
                placeholder="Type a message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                className="border p-2 flex-1"
              />
              <button
                className="bg-blue-500 text-white p-2 ml-2"
                onClick={sendMessage}
              >
                Send
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
