import React, { useEffect, useState } from "react";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userId, setUserId] = useState(0);
  const [receiverId, setReceiverId] = useState(0); // 0 = group chat
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [users, setUsers] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    startConnection();

    connection.on("ReceiveMessage", (senderId, message) => {
      setChat(prev => [...prev, { senderId, message }]);
    });

    connection.on("ReceiveUserList", (usersList) => {
      setUsers(usersList);
    });

    return () => {
      connection.off("ReceiveMessage");
      connection.off("ReceiveUserList");
    };
  }, []);

  async function login() {
    if (!username || !password) return alert("Enter username and password");

    try {
      // Call API to get UserId
      const res = await fetch("http://localhost:5090/api/users/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password })
      });

      if (!res.ok) return alert("Invalid credentials");

      const id = await res.json();
      setUserId(id);
      setLoggedIn(true);

      // SignalR login
      if (connection.state === 1) { // Connected
        await connection.invoke("Login", id);
        await connection.invoke("GetAllUsers"); // get user list
      }

    } catch (err) {
      console.error("Login failed:", err);
    }
  }

  async function sendMessage() {
    if (message.trim() === "") return;

    if (connection.state === 1) {
      try {
        await connection.invoke("SendMessage", receiverId, message);
        setMessage("");
      } catch (err) {
        console.error("Error sending message:", err);
      }
    } else {
      console.warn("Connection not ready, message not sent.");
    }
  }

  return (
    <div className="flex h-screen">
      {!loggedIn ? (
        <div className="m-auto p-4 border rounded shadow">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={e => setUsername(e.target.value)}
            className="border p-2 mb-2 w-full"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            className="border p-2 mb-2 w-full"
          />
          <button onClick={login} className="bg-green-500 text-white p-2 w-full rounded">
            Login
          </button>
        </div>
      ) : (
        <>
          {/* User List */}
          <div className="w-1/4 border-r p-2 overflow-y-auto">
            <h2 className="font-bold mb-2">Users</h2>
            <div
              className={`p-2 ${receiverId === 0 ? 'bg-blue-200' : ''}`}
              onClick={() => setReceiverId(0)}
            >
              Group Chat
            </div>
            {users.filter(u => u.id !== userId).map(u => (
              <div
                key={u.id}
                className={`p-2 ${receiverId === u.id ? 'bg-blue-200' : ''}`}
                onClick={() => setReceiverId(u.id)}
              >
                {u.username}
              </div>
            ))}
          </div>

          {/* Chat Window */}
          <div className="flex-1 flex flex-col p-2">
            <div className="flex-1 overflow-y-auto border p-2 mb-2 bg-white rounded shadow">
              {chat.map((c, i) => (
                <div key={i} className={`p-1 ${c.senderId === userId ? 'text-right' : 'text-left'}`}>
                  <strong>{c.senderId === userId ? "You" : users.find(u => u.id === c.senderId)?.username}: </strong>
                  {c.message}
                </div>
              ))}
            </div>

            <div className="flex">
              <input
                className="border p-2 flex-1"
                placeholder="Type a message..."
                value={message}
                onChange={e => setMessage(e.target.value)}
                onKeyDown={e => e.key === "Enter" && sendMessage()}
              />
              <button className="bg-blue-500 text-white p-2 ml-2 rounded" onClick={sendMessage}>
                Send
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatWindow;
