import React, { useEffect, useState } from "react";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = ({ currentUser, onLogout }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState("");
  const [allChats, setAllChats] = useState({});
  const [notifications, setNotifications] = useState({});
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    const init = async () => {
      try {
        await startConnection();

        connection.onreconnected(() => setIsConnected(true));
        connection.onclose(() => setIsConnected(false));

        if (connection.state === "Connected") setIsConnected(true);

        await connection.invoke("Login", currentUser.id);

        const res = await fetch("http://localhost:5090/api/users");
        const data = await res.json();
        setUsers(data.filter((u) => u.id !== currentUser.id));

        // Receive message handler
        connection.on("ReceiveMessage", (senderId, text) => {
          const chatKey = senderId;
          setAllChats((prev) => ({
            ...prev,
            [chatKey]: [...(prev[chatKey] || []), { senderId, message: text }],
          }));

          // notification if chat not open
          if (!selectedUser || selectedUser.id !== senderId) {
            setNotifications((prev) => ({
              ...prev,
              [senderId]: (prev[senderId] || 0) + 1,
            }));
          }
        });
      } catch (err) {
        console.error("SignalR connection failed:", err);
      }
    };

    init();
    return () => connection.off("ReceiveMessage");
  }, [currentUser, selectedUser]);

  const sendMessage = async () => {
    if (!message.trim() || !selectedUser) return;
    if (connection.state !== "Connected") return console.warn("Connection not ready");

    await connection.invoke("SendMessage", selectedUser.id, message);

    setAllChats((prev) => ({
      ...prev,
      [selectedUser.id]: [
        ...(prev[selectedUser.id] || []),
        { senderId: currentUser.id, message },
      ],
    }));

    setMessage("");
  };

  const handleUserClick = (user) => {
    setSelectedUser(user);
    setNotifications((prev) => ({ ...prev, [user.id]: 0 }));
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-1/4 border-r p-4 overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="font-bold">Users</h2>
          <button
            className="text-sm bg-red-500 text-white px-2 py-1 rounded"
            onClick={() => {
              sessionStorage.removeItem("currentUser");
              onLogout();
            }}
          >
            Logout
          </button>
        </div>

        {users.map((user) => (
          <div
            key={user.id}
            className={`flex justify-between items-center p-2 cursor-pointer rounded ${
              selectedUser?.id === user.id ? "bg-gray-200" : "hover:bg-gray-100"
            }`}
            onClick={() => handleUserClick(user)}
          >
            <span>{user.username}</span>
            {notifications[user.id] > 0 && (
              <span className="bg-red-500 text-white rounded-full text-xs px-2">
                {notifications[user.id]}
              </span>
            )}
          </div>
        ))}
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col p-4">
        {selectedUser ? (
          <>
            <h2 className="font-bold mb-2">{selectedUser.username}</h2>
            <div className="flex-1 overflow-y-auto border p-2 rounded mb-2">
              {(allChats[selectedUser.id] || []).map((c, i) => (
                <div
                  key={i}
                  className={`mb-1 ${
                    c.senderId === currentUser.id ? "text-right" : "text-left"
                  }`}
                >
                  <strong>
                    {c.senderId === currentUser.id ? "You" : selectedUser.username}:
                  </strong>{" "}
                  {c.message}
                </div>
              ))}
            </div>
            <div className="flex">
              <input
                className="flex-1 border p-2 rounded"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button
                className="ml-2 p-2 bg-blue-500 text-white rounded"
                onClick={sendMessage}
              >
                Send
              </button>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-400">
            Select a user to start chat
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
