import React, { useEffect, useState } from "react";
import connection from '../services/signalRConnection';
import * as signalR from '@microsoft/signalr';

const ChatWindow = () => {
  const [user, setUser] = useState("");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    // Listen for messages
    connection.on("ReceiveMessage", (user, message) => {
      setChat(prev => [...prev, { user, message }]);
    });

    // Check connection state
    const checkConnection = () => {
      if (connection.state === signalR.HubConnectionState.Connected) {
        setConnected(true);
      } else {
        setTimeout(checkConnection, 500);
      }
    };
    checkConnection();

    // Cleanup
    return () => {
      connection.off("ReceiveMessage");
    };
  }, []);

  const sendMessage = async () => {
    if (!user || !message) return;

    if (connected) {
      try {
        await connection.invoke("SendMessage", user, message);
        setMessage("");
      } catch (err) {
        console.error("Error sending message: ", err);
      }
    } else {
      console.warn("Connection not ready yet. Please wait...");
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100 p-4">
      {/* Username input */}
      <div className="flex mb-2">
        <input
          className="border p-2 flex-1"
          placeholder="Your name..."
          value={user}
          onChange={e => setUser(e.target.value)}
        />
      </div>

      {/* Chat messages */}
      <div className="flex-1 overflow-y-auto bg-white p-3 rounded shadow">
        {chat.map((c, i) => (
          <div key={i} className={`p-2 ${c.user === user ? 'text-right' : 'text-left'}`}>
            <strong>{c.user}: </strong>{c.message}
          </div>
        ))}
      </div>

      {/* Message input */}
      <div className="flex mt-3">
        <input
          className="border p-2 flex-1"
          placeholder={connected ? "Type a message..." : "Connecting..."}
          value={message}
          onChange={e => setMessage(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
          disabled={!connected}
        />
        <button
          className="bg-blue-500 text-white p-2 ml-2 rounded"
          onClick={sendMessage}
          disabled={!connected}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
