import React, { useEffect, useState } from "react";
import { connection } from "../services/signalRConnection";

const ChatWindow = () => {
  const [user, setUser] = useState("");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    connection.start().then(() => {
      console.log("Connected!");
      connection.on("ReceiveMessage", (user, message) => {
        setChat(prev => [...prev, { user, message }]);
      });
    });
  }, []);

  const sendMessage = async () => {
    if (message && user) {
      await connection.invoke("SendMessage", user, message);
      setMessage("");
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100 p-4">
      <div className="flex mb-2">
        <input className="border p-2 flex-1" placeholder="Your name..." value={user} onChange={e => setUser(e.target.value)} />
      </div>

      <div className="flex-1 overflow-y-auto bg-white p-3 rounded shadow">
        {chat.map((c, i) => (
          <div key={i} className={`p-2 ${c.user === user ? 'text-right' : 'text-left'}`}>
            <strong>{c.user}: </strong>{c.message}
          </div>
        ))}
      </div>

      <div className="flex mt-3">
        <input
          className="border p-2 flex-1"
          placeholder="Type a message..."
          value={message}
          onChange={e => setMessage(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
        />
        <button className="bg-blue-500 text-white p-2 ml-2 rounded" onClick={sendMessage}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
