import React, { useEffect, useState } from "react";
import connection, { startConnection } from "../services/signalRConnection";

const ChatWindow = ({ userId }) => {
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [receiverId, setReceiverId] = useState(0); // 0 = group chat

  useEffect(() => {
    startConnection(userId);

    connection.on("ReceiveMessage", (senderId, message) => {
      setChat(prev => [...prev, { senderId, message }]);
    });

    return () => {
      connection.off("ReceiveMessage");
    };
  }, [userId]);

  async function sendMessage() {
    if (message.trim() === "") return;
    if (connection.state === 1) { // Connected
      await connection.invoke("SendMessage", receiverId, message);
      setMessage("");
    } else {
      console.warn("Connection not ready, message not sent.");
    }
  }

  return (
    <div>
      <div>
        {chat.map((c, i) => (
          <div key={i}>
            <strong>{c.senderId === userId ? "You" : c.senderId}: </strong>
            {c.message}
          </div>
        ))}
      </div>
      <input value={message} onChange={e => setMessage(e.target.value)}
             onKeyDown={e => e.key === "Enter" && sendMessage()} />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatWindow;
