import React, { useEffect, useState, useRef } from "react";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = ({ currentUser, onLogout }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState("");
  const [allChats, setAllChats] = useState({});
  const [notifications, setNotifications] = useState({});
  const [isConnected, setIsConnected] = useState(false);

  const messagesEndRef = useRef(null);

  // Scroll chat to bottom
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [allChats, selectedUser]);

  useEffect(() => {
    const init = async () => {
      try {
        if (connection.state === "Disconnected" || connection.state === 0) {
          await startConnection();
        }

        connection.off("ReceiveMessage");
        connection.onreconnected(() => setIsConnected(true));
        connection.onclose(() => setIsConnected(false));

        if (connection.state === "Connected" || connection.state === 1)
          setIsConnected(true);

        await connection.invoke("Login", currentUser.id);

        const res = await fetch("http://localhost:5090/api/users");
        const data = await res.json();
        setUsers(data.filter((u) => u.id !== currentUser.id));

        const usersMap = Object.fromEntries(data.map(u => [u.id, u.username]));

        connection.on("ReceiveMessage", (senderId, receiverId, messageText) => {
          const otherUserId = senderId === currentUser.id ? receiverId : senderId;
          const msg = {
            senderId,
            message: messageText?.toString() || "",
            senderUsername: usersMap[senderId] || "Unknown",
          };

          setAllChats((prev) => ({
            ...prev,
            [otherUserId]: [...(prev[otherUserId] || []), msg],
          }));

          if (!selectedUser || selectedUser.id !== senderId) {
            setNotifications((prev) => ({
              ...prev,
              [senderId]: (prev[senderId] || 0) + 1,
            }));
          }
        });
      } catch (err) {
        console.error("SignalR connection failed:", err);
      }
    };
    init();
    return () => {
      connection.off("ReceiveMessage");
      connection.onclose(null);
      connection.onreconnected(null);
    };
  }, [currentUser]);

  const sendMessage = async () => {
    if (!message.trim() || !selectedUser) return;
    if (connection.state !== "Connected" && connection.state !== 1) return;

    try {
      await connection.invoke("SendMessage", selectedUser.id, message);

      const msg = { senderId: currentUser.id, message, senderUsername: currentUser.username };
      setAllChats((prev) => ({
        ...prev,
        [selectedUser.id]: [...(prev[selectedUser.id] || []), msg],
      }));

      setMessage("");
    } catch (err) {
      console.error("SendMessage failed:", err);
    }
  };

  const handleUserClick = async (user) => {
    setSelectedUser(user);
    setNotifications((prev) => ({ ...prev, [user.id]: 0 }));
    sessionStorage.setItem("lastSelectedUser", JSON.stringify(user));

    try {
      const res = await fetch(`http://localhost:5090/api/users/messages/${currentUser.id}/${user.id}`);
      const msgs = await res.json();
      const formatted = msgs.map(m => ({
        senderId: m.senderId,
        message: m.text,
        senderUsername: m.senderId === currentUser.id ? currentUser.username : user.username
      }));
      setAllChats((prev) => ({ ...prev, [user.id]: formatted }));
    } catch (err) {
      console.error("Failed to fetch messages:", err);
    }
  };

  useEffect(() => {
    const lastUser = sessionStorage.getItem("lastSelectedUser");
    if (lastUser) handleUserClick(JSON.parse(lastUser));
  }, []);

  const handleLogoutClick = () => {
    sessionStorage.removeItem("currentUser");
    sessionStorage.removeItem("lastSelectedUser");
    onLogout();
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-1/4 border-r p-4 overflow-y-auto bg-white shadow">
        <div className="flex justify-between items-center mb-4">
          <h2 className="font-bold text-lg">{currentUser.username}</h2>
          <button
            onClick={handleLogoutClick}
            className="text-sm bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
          >
            Logout
          </button>
        </div>
        {users.map((user) => (
          <div
            key={user.id}
            onClick={() => handleUserClick(user)}
            className={`flex justify-between items-center p-2 rounded cursor-pointer mb-1 ${
              selectedUser?.id === user.id ? "bg-blue-100" : "hover:bg-gray-200"
            }`}
          >
            <span className="font-medium">{user.username}</span>
            {notifications[user.id] > 0 && (
              <span className="bg-red-500 text-white rounded-full text-xs px-2 py-0.5">
                {notifications[user.id]}
              </span>
            )}
          </div>
        ))}
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col p-4">
        {selectedUser ? (
          <>
            <h2 className="font-bold mb-2 text-lg border-b pb-2">{selectedUser.username}</h2>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-3 bg-gray-50 rounded-lg shadow-inner mb-2 flex flex-col space-y-2">
              {(allChats[selectedUser.id] || []).map((c, i) => (
                <div
                  key={i}
                  className={`max-w-[70%] p-2 rounded-lg shadow ${
                    c.senderId === currentUser.id
                      ? "self-end bg-blue-500 text-white"
                      : "self-start bg-white text-gray-800"
                  }`}
                >
                  {c.message}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <div className="flex items-center space-x-2">
              <input
                className="flex-1 border rounded-full p-2 px-4 focus:outline-none focus:ring-2 focus:ring-blue-400"
                placeholder="Message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button
                onClick={sendMessage}
                disabled={!isConnected || !selectedUser}
                className={`px-4 py-2 rounded-full text-white ${
                  isConnected ? "bg-blue-500 hover:bg-blue-600" : "bg-gray-400 cursor-not-allowed"
                }`}
              >
                Send
              </button>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-400 text-lg">
            Select a user to start chat
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
