import React, { useEffect, useState } from "react";
import { connection, startConnection } from '../services/signalRConnection';

const ChatWindow = () => {
  const [userId, setUserId] = useState(0); // login user ID
  const [receiverId, setReceiverId] = useState(0); // 0 = group chat
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    startConnection();

    // Receive messages
    connection.on("ReceiveMessage", (senderId, message) => {
      setChat(prev => [...prev, { senderId, message }]);
    });

    return () => {
      connection.off("ReceiveMessage");
    };
  }, []);

  async function login() {
    if (userId <= 0) return alert("Enter a valid user ID");
    try {
      await connection.invoke("Login", userId);
      setLoggedIn(true);
      console.log(`Logged in as user ${userId}`);
    } catch (err) {
      console.error("Login failed: ", err);
    }
  }

  async function sendMessage() {
    if (message.trim() === "") return;

    if (connection.state === 1) { // Connected
      try {
        await connection.invoke("SendMessage", receiverId, message);
        setMessage("");
      } catch (err) {
        console.error("Error sending message: ", err);
      }
    } else {
      console.warn("Connection not ready, message not sent.");
    }
  }

  return (
    <div className="flex flex-col h-screen bg-gray-100 p-4">
      {!loggedIn ? (
        <div className="mb-4">
          <input
            type="number"
            className="border p-2 mr-2"
            placeholder="Your user ID..."
            value={userId}
            onChange={e => setUserId(parseInt(e.target.value))}
          />
          <button className="bg-green-500 text-white p-2 rounded" onClick={login}>
            Login
          </button>
        </div>
      ) : (
        <>
          <div className="flex mb-2">
            <input
              type="number"
              className="border p-2 mr-2"
              placeholder="Receiver ID (0 = group)"
              value={receiverId}
              onChange={e => setReceiverId(parseInt(e.target.value))}
            />
          </div>

          <div className="flex-1 overflow-y-auto bg-white p-3 rounded shadow">
            {chat.map((c, i) => (
              <div key={i} className={`p-2 ${c.senderId === userId ? 'text-right' : 'text-left'}`}>
                <strong>{c.senderId}: </strong>{c.message}
              </div>
            ))}
          </div>

          <div className="flex mt-3">
            <input
              className="border p-2 flex-1"
              placeholder="Type a message..."
              value={message}
              onChange={e => setMessage(e.target.value)}
              onKeyDown={e => e.key === "Enter" && sendMessage()}
            />
            <button className="bg-blue-500 text-white p-2 ml-2 rounded" onClick={sendMessage}>
              Send
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatWindow;
