// src/components/ChatWindow.jsx
import React, { useEffect, useState } from "react";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = ({ currentUser }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState("");
  const [allChats, setAllChats] = useState({}); // userId -> messages array
  const [notifications, setNotifications] = useState({}); // userId -> unread count
  const [isConnected, setIsConnected] = useState(false);

  // Initialize connection, fetch users and listen for messages
  useEffect(() => {
    const init = async () => {
      try {
        await startConnection();

        connection.onreconnected(() => setIsConnected(true));
        connection.onclose(() => setIsConnected(false));
        if (connection.state === 1) setIsConnected(true);

        // login current user
        await connection.invoke("Login", currentUser.id);

        // fetch users (excluding self)
        const res = await fetch("http://localhost:5090/api/users");
        const data = await res.json();
        setUsers(data.filter(u => u.id !== currentUser.id));

        // listen for incoming messages
        connection.on("ReceiveMessage", async (senderId, receiverId, text) => {
          const otherUserId = senderId === currentUser.id ? receiverId : senderId;

          // update chat
          setAllChats(prev => ({
            ...prev,
            [otherUserId]: [...(prev[otherUserId] || []), { senderId, message: text }]
          }));

          // show notification if chat not open
          if (selectedUser?.id !== otherUserId) {
            setNotifications(prev => ({
              ...prev,
              [otherUserId]: (prev[otherUserId] || 0) + 1
            }));
          }
        });

      } catch (err) {
        console.error("SignalR connection failed:", err);
      }
    };

    init();

    return () => {
      connection.off("ReceiveMessage");
    };
  }, [currentUser, selectedUser]);

  // Open chat with a user and load previous messages
  const openChat = async (user) => {
    setSelectedUser(user);

    // clear notifications for this user
    setNotifications(prev => ({ ...prev, [user.id]: 0 }));

    // fetch previous messages from API
    try {
      const res = await fetch(`http://localhost:5090/api/users/messages/${currentUser.id}/${user.id}`);
      const data = await res.json();
      setAllChats(prev => ({
        ...prev,
        [user.id]: data.map(m => ({ senderId: m.senderId, message: m.text }))
      }));
    } catch (err) {
      console.error("Failed to fetch previous messages:", err);
    }
  };

  const sendMessage = async () => {
    if (!message.trim() || !selectedUser) return;

    if (!isConnected) {
      console.warn("Connection not ready");
      return;
    }

    try {
      // send via SignalR
      await connection.invoke("SendMessage", selectedUser.id, message);

      // update local chat immediately
      setAllChats(prev => ({
        ...prev,
        [selectedUser.id]: [...(prev[selectedUser.id] || []), { senderId: currentUser.id, message }]
      }));

      setMessage("");
    } catch (err) {
      console.error("Failed to send message:", err);
    }
  };

  return (
    <div className="flex h-screen">
      {/* User List */}
      <div className="w-1/4 border-r p-4 overflow-y-auto">
        <h2 className="font-bold mb-4">Users</h2>
        {users.map(user => (
          <div
            key={user.id}
            className={`p-2 cursor-pointer flex justify-between items-center ${selectedUser?.id === user.id ? "bg-gray-200" : ""}`}
            onClick={() => openChat(user)}
          >
            <span>{user.username}</span>
            {notifications[user.id] > 0 && (
              <span className="bg-red-500 text-white text-xs rounded-full px-2">{notifications[user.id]}</span>
            )}
          </div>
        ))}
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col p-4">
        {selectedUser ? (
          <>
            <h2 className="font-bold mb-2">{selectedUser.username}</h2>
            <div className="flex-1 overflow-y-auto border p-2 rounded mb-2">
              {(allChats[selectedUser.id] || []).map((c, i) => (
                <div key={i} className={`my-1 ${c.senderId === currentUser.id ? "text-right" : "text-left"}`}>
                  <strong>{c.senderId === currentUser.id ? "You" : selectedUser.username}: </strong>
                  {c.message}
                </div>
              ))}
            </div>
            <div className="flex">
              <input
                className="flex-1 border p-2 rounded"
                value={message}
                onChange={e => setMessage(e.target.value)}
                onKeyDown={e => e.key === "Enter" && sendMessage()}
                disabled={!isConnected}
              />
              <button
                className="ml-2 p-2 bg-blue-500 text-white rounded disabled:opacity-50"
                onClick={sendMessage}
                disabled={!isConnected}
              >
                Send
              </button>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-500">
            Select a user to chat
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
