import React, { useEffect, useState, useRef } from "react";
import { connection, startConnection } from "../services/signalRConnection";

const ChatWindow = ({ currentUser, onLogout }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState("");
  const [allChats, setAllChats] = useState({});
  const [notifications, setNotifications] = useState({});
  const [isConnected, setIsConnected] = useState(false);
  const [chatBackground, setChatBackground] = useState("#f0f0f0");

  const messagesEndRef = useRef(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [allChats, selectedUser]);

  // Initialize SignalR
  useEffect(() => {
    const init = async () => {
      try {
        if (connection.state === "Disconnected" || connection.state === 0) await startConnection();

        setIsConnected(connection.state === "Connected" || connection.state === 1);
        await connection.invoke("Login", currentUser.id);

        // Fetch users
        const res = await fetch("http://localhost:5090/api/users");
        const data = await res.json();
        setUsers(data.filter(u => u.id !== currentUser.id));
        const usersMap = Object.fromEntries(data.map(u => [u.id, u.username]));

        // Receive messages
        connection.off("ReceiveMessage");
        connection.on("ReceiveMessage", (senderId, receiverId, messageText, messageType) => {
          const otherUserId = senderId === currentUser.id ? receiverId : senderId;
          const msg = {
            senderId,
            message: messageText,
            type: messageType || "text",
            senderUsername: usersMap[senderId] || "Unknown",
          };

          setAllChats(prev => ({
            ...prev,
            [otherUserId]: [...(prev[otherUserId] || []), msg],
          }));

          if (!selectedUser || selectedUser.id !== senderId) {
            setNotifications(prev => ({
              ...prev,
              [senderId]: (prev[senderId] || 0) + 1,
            }));
          }
        });
      } catch (err) {
        console.error("SignalR init failed:", err);
      }
    };
    init();

    return () => {
      connection.off("ReceiveMessage");
    };
  }, [currentUser, selectedUser]);

  // Send message
  const sendMessage = async (msg, type = "text") => {
    if (!msg && type === "text") return;
    if (!selectedUser) return;
    if (connection.state !== "Connected" && connection.state !== 1) return;

    await connection.invoke("SendMessage", selectedUser.id, msg, type);

    setAllChats(prev => ({
      ...prev,
      [selectedUser.id]: [...(prev[selectedUser.id] || []), {
        senderId: currentUser.id,
        message: msg,
        type,
        senderUsername: currentUser.username
      }],
    }));

    if (type === "text") setMessage("");
  };

  // Select user & load chat
  const handleUserClick = async (user) => {
    setSelectedUser(user);
    setNotifications(prev => ({ ...prev, [user.id]: 0 }));
    sessionStorage.setItem("lastSelectedUser", JSON.stringify(user));

    try {
      const res = await fetch(`http://localhost:5090/api/users/messages/${currentUser.id}/${user.id}`);
      const msgs = await res.json();
      const formatted = msgs.map(m => ({
        senderId: m.senderId,
        message: m.text,
        type: m.type || "text",
        senderUsername: m.senderId === currentUser.id ? currentUser.username : user.username
      }));
      setAllChats(prev => ({ ...prev, [user.id]: formatted }));
    } catch (err) {
      console.error("Failed to fetch messages:", err);
    }
  };

  // Restore last selected user
  useEffect(() => {
    const lastUser = sessionStorage.getItem("lastSelectedUser");
    if (lastUser) handleUserClick(JSON.parse(lastUser));
  }, []);

  // Upload file or image
  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5090/api/files/upload", {
        method: "POST",
        body: formData
      });
      const data = await res.json();
      const type = file.type.startsWith("image/") ? "image" : "file";
      sendMessage(data.fileUrl, type);
    } catch (err) {
      console.error("File upload failed:", err);
    } finally {
      e.target.value = null;
    }
  };

  const handleLogoutClick = () => {
    sessionStorage.removeItem("currentUser");
    sessionStorage.removeItem("lastSelectedUser");
    onLogout();
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-1/4 border-r p-4 overflow-y-auto bg-white shadow">
        <div className="flex justify-between items-center mb-4">
          <h2 className="font-bold text-lg">{currentUser.username}</h2>
          <button
            onClick={handleLogoutClick}
            className="text-sm bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
          >Logout</button>
        </div>
        {users.map(u => (
          <div
            key={u.id}
            onClick={() => handleUserClick(u)}
            className={`flex justify-between items-center p-2 rounded cursor-pointer mb-1 ${
              selectedUser?.id === u.id ? "bg-blue-100" : "hover:bg-gray-200"
            }`}
          >
            <span className="font-medium">{u.username}</span>
            {notifications[u.id] > 0 && (
              <span className="bg-red-500 text-white rounded-full text-xs px-2 py-0.5">
                {notifications[u.id]}
              </span>
            )}
          </div>
        ))}
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col p-4">
        {selectedUser ? (
          <>
            <div className="flex justify-between items-center mb-2">
              <h2 className="font-bold text-lg border-b pb-2">{selectedUser.username}</h2>
              <input type="color" value={chatBackground} onChange={e => setChatBackground(e.target.value)}
                className="w-10 h-8 rounded cursor-pointer" title="Change Chat Background" />
            </div>

            <div className="flex-1 overflow-y-auto p-3 rounded-lg shadow-inner flex flex-col space-y-2"
              style={{ backgroundColor: chatBackground }}>
              {(allChats[selectedUser.id] || []).map((c, i) => (
                <div key={i}
                  className={`max-w-[70%] p-2 rounded-lg shadow break-words ${
                    c.senderId === currentUser.id ? "self-end bg-blue-500 text-white" : "self-start bg-white text-gray-800"
                  }`}
                >
                  {c.type === "text" && <span>{c.message}</span>}
                  {c.type === "image" && <img src={c.message} className="rounded max-w-full" alt="sent" />}
                  {c.type === "file" && <a href={c.message} target="_blank" rel="noreferrer" className="underline text-blue-700">Download File</a>}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <div className="flex items-center space-x-2 mt-2">
              <input
                className="flex-1 border rounded-full p-2 px-4 focus:outline-none focus:ring-2 focus:ring-blue-400"
                placeholder="Message..."
                value={message}
                onChange={e => setMessage(e.target.value)}
                onKeyDown={e => e.key === "Enter" && sendMessage(message, "text")}
              />

              {/* Attach File */}
              <label className="cursor-pointer bg-gray-200 p-2 rounded-full hover:bg-gray-300">
                ðŸ“Ž
                <input type="file" className="hidden" onChange={handleFileChange} />
              </label>

              {/* Take Picture */}
              <label className="cursor-pointer bg-gray-200 p-2 rounded-full hover:bg-gray-300">
                ðŸ“¸
                <input type="file" className="hidden" onChange={handleFileChange} accept="image/*" capture="environment"/>
              </label>

              <button onClick={() => sendMessage(message, "text")}
                className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600">Send</button>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-400 text-lg">Select a user to start chat</div>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
